import { PayloadAction, createSlice } from "@reduxjs/toolkit";
import { productsType } from "../types/Types";

export const cartSlice = createSlice({
    name: "cart",
    initialState: [],
    reducers: {
        addCart: (state: productsType[], action: PayloadAction<productsType>): any => {
            const cart: productsType[]  = [...state, action.payload]
            return cart
        },

        updateCart: (state: productsType[], action: PayloadAction<productsType>): any => {
            const cart = state.filter((item: productsType) => item.id !== action.payload.id)
            return [...cart, action.payload]
        },
        
        removeItem: (state: productsType[], action: PayloadAction<productsType>): any => {
            const cart = state.filter((item: productsType) => item.id !== action.payload.id)
            return cart
        },
        addItem:(state: productsType[], action: PayloadAction<any>): any=>  {
            return [
              ...state.slice(0, state.indexOf(action.payload)),
              {count: action.payload.count + 1},
              ...state.slice(state.indexOf(action.payload))
            ]
        }
    }
})

export const {addCart, updateCart, removeItem, addItem} = cartSlice.actions
export const cartReducer =  cartSlice.reducer
