import { useEffect } from "react"
import { useAxios } from "./Hooks/useAxios"
import { useDispatch, useSelector } from "react-redux"
import { CurrentType } from "./types/Types"

export const useApp = () => {
    const {current} = useSelector((state: {current: CurrentType}) => state.current)
    const currentUser = !!Object.keys(current).length
    const {loading, fetchUsers} = useAxios()
    
    useEffect(()=> {fetchUsers()}, [])
    //users
    return {current, currentUser, loading}
}
