import { PayloadAction, createSlice, current } from "@reduxjs/toolkit";
import { productsType } from "../types/Types";


export const cartSlice = createSlice({
    name: "cart",
    initialState: [],
    reducers: {
        addCart: (state: productsType[], action: PayloadAction<productsType>): any => {
            const cart: productsType[]  = [...state, action.payload]
            return cart
        },
        updateCart: (state: productsType[], action: PayloadAction<any>): any => {
            
            const cart = current(state)
            const index = cart.indexOf(action.payload[0])
            const start = cart.slice(0, index)
            const end = cart.slice(index + 1)
            // console.log(action.payload)
            // console.log(index)
            return  [...start, {...action.payload, count: action.payload.count + 1} ,...end]
        },
        removeItem: (state: productsType[], action: PayloadAction<productsType>): any => {
            const cart = state.filter((item: productsType) => item.id !== action.payload.id)
            return cart
        },
    }
})

export const {addCart, updateCart, removeItem} = cartSlice.actions
export const cartReducer =  cartSlice.reducer
