import { useDispatch } from "react-redux"
import { updateCart, removeItem } from "../../Slices/cartSlice"
import { CartFunctions, ProductsType } from "../../types/Types"

export const useCart = () => {
  const dispatch = useDispatch()
  
  function editCart(product: ProductsType, arg2?: CartFunctions){
    if(product && !arg2){
      dispatch(removeItem(arg))
    }else if(product && arg2){
      if(arg2 === CartFunctions.plusCount){
        dispatch(updateCart({...product, count: product.count + 1, price: product.price + (product.price / product.count)}))
      }else if(arg2 === CartFunctions.minusCount){
        dispatch(updateCart({...product, count: product.count - 1, price: product.price - (product.price / product.count)}))
        product.count < 2 && dispatch(removeItem(product))
      }
    }
  }
  
  return {editCart}
}
