import { createSlice } from "@reduxjs/toolkit";

export const cartSlice = createSlice({
    name: "cart",
    initialState: [],
    reducers: {
        addCart: (state: any, action: any): any => {
            const cart = [...state ,action.payload]
            return cart
        },
        updateCart: (state: any, action: any): any => {
            const cart = state.filter((item: any) => item.id !== action.payload.id)
            return [...cart, action.payload]
        },
        removeItem: (state: any, action: any): any => {
            const cart = state.filter((item: any) => item.id !== action.payload.id)
            return [action.payload]
        }
    }
})

export const {addCart, updateCart} = cartSlice.actions
export const cartReducer =  cartSlice.reducer
