import { useDispatch, useSelector } from "react-redux"
import { addCart, updateCart } from "../../Slices/cartSlice"
import { productsType } from "../../types/Types"

export const useItem = () => {
  const cart = useSelector((state: any) => state.cart)
    const dispatch = useDispatch()

    const handleClick = (arg: productsType) => {
        const filteredItem: productsType = cart.find((item: productsType) => item.id === arg.id)
        if(!filteredItem){
          dispatch(addCart(arg))
        }else{
          dispatch(updateCart({...arg, count: filteredItem.count! + 1}))
        }
      }
    return {handleClick}      
}
