import { PayloadAction, createSlice, current } from "@reduxjs/toolkit";
import { productsType } from "../types/Types";


export const cartSlice = createSlice({
    name: "cart",
    initialState: [],
    reducers: {
        addCart: (state: productsType[], action: PayloadAction<productsType>): any => [...state, action.payload],
        updateCart: (state: productsType[], action: PayloadAction<any>): any => {
            const cart = current(state)
            const index = cart.indexOf(action.payload[0])
            const data: productsType = action.payload[0]
            const start = cart.slice(0, index)
            const end = cart.slice(index + 1)
            
            console.log(start)
            console.log(data)
            console.log(end)
            if(data){
                const num = action.payload[1]
                return  [...start, {...data, count: data.count + num, price: data.price + num * (data.price / data.count)}, ...end]
            }
                // TODO: urish dzev hashvel aysqan erkar kodic xusapelu hamar
        },
        removeItem: (state: productsType[], action: PayloadAction<productsType>): any => state.filter((item: productsType) => item.id !== action.payload.id),
    }
})

export const {addCart, updateCart, removeItem} = cartSlice.actions
export const cartReducer =  cartSlice.reducer
