import { createSlice } from "@reduxjs/toolkit";

export const cartSlice = createSlice({
    name: "cart",
    initialState: [],
    reducers: {
        addCart: (state: any, action: any): any => {
            const cart = [...state ,action.payload]
            return cart
        },
        cartCount: (state: any, action: any): any => {
            if(!action.count){
                const x = state.cart.count = 1
                console.log(x)
            }else{
                const x = state.cart.count += 1
                console.log(x)
            }
        }
    }
})

export const {addCart, cartCount} = cartSlice.actions
export const cartReducer =  cartSlice.reducer
