import { useEffect } from "react"
import { apis } from "./Apis/apis"
import { useAxios } from "./Hooks/useAxios"
import { useDispatch, useSelector } from "react-redux"
import { setUser } from "./Slices/usersSlice"
import { CurrentType } from "./types/Types"
import { filterUsersData } from "./helpers/helper"

export const useApp = () => {
    const {current} = useSelector((state: {current: CurrentType}) => state.current)
    const currentUser = !!Object.keys(current).length
    const dispatch = useDispatch()
    const {loading, allData, fetchData} = useAxios(apis.users)
    useEffect(() => {fetchData().then((res: any)=> dispatch(setUser(filterUsersData(res.data)|| {})))}, [])
    
    return {allData, current, currentUser, loading}
}
