import { useDispatch } from "react-redux"
import { updateCart, removeItem } from "../../Slices/cartSlice"
import { CartFunctions, ProductsType } from "../../types/Types"

export const useCart = () => {
  const dispatch = useDispatch()
  
  function editCart(arg: ProductsType, arg2?: CartFunctions){
    if(arg && !arg2){
      dispatch(removeItem(arg))
    }else if(arg && arg2){
      if(arg2 === CartFunctions.plusCount){
        dispatch(updateCart({...arg, count: arg.count + 1}))
      }else if(arg2 === CartFunctions.minusCount){
        dispatch(updateCart({...arg, count: arg.count - 1}))
        arg.count < 2 && dispatch(removeItem(arg))
      }
    }
  }
  
  return {editCart}
}
