import { PayloadAction, createSlice } from "@reduxjs/toolkit";
import { productsType, usersType } from "../types/Types";

const currentUserSlice = createSlice({
  name: "currentUser",
  initialState: {},
  reducers: {
    setCurrentUser: (state: any, action: PayloadAction<usersType>): usersType => action.payload,
    deleteCurrentUser: (state: any): productsType => {
      id: -1;
      description: "";
      image: "";
      price: 0;
      title: "";
      count: 0;}
  },
  
});

export const { setCurrentUser, deleteCurrentUser} = currentUserSlice.actions;
export const currentReducer = currentUserSlice.reducer;
