import React from 'react'
import { useSelector } from 'react-redux'
import { useItem } from './Item.hook'
import { productsType, stateType } from '../../types/Types'
import { useCart } from '../Cart/Cart.hook'

const Item = ({item}: {item : productsType}) => {
  const cart = useSelector((state: stateType) => state.cart)
  const {handleClick} = useItem(cart)
  const {plusItem} = useCart()

  return (
    <>
      <div style={{display: "grid", gridTemplateColumns: "3fr 1fr 1fr"}}>
        <h5>{item.title}</h5>
      <div>
        {/* <img style={{height: "50px"}} src = {item.image}/> */}
      </div>
        <p>{item.price} $</p>
    </div>
      <button onClick={() => plusItem(item)}>ADD</button>
    </>
  )
}

export default Item
