import { useEffect } from "react"
import { apis } from "../../Apis/apis"
import { useAxios } from "../../Hooks/useAxios"
import { useDispatch } from "react-redux"
import { getItems } from "../../Slices/ItemsSlice"
import { filterProductsData } from "../../helpers/helper"

export const useItem = () => {
    const dispatch = useDispatch()
    const useFetch = useAxios()
    const [loading, data, error] = useFetch(apis.products)
    // unused valiables
    // const [loading, error, data] = useAxios(apis.products)
    
    useEffect(() => {
        useFetch(apis.products)
        dispatch(getItems(filterProductsData(data)))
    }, [data])

    return {data: filterProductsData(data), loading}
}
