import { createSlice } from "@reduxjs/toolkit";
import { productsType } from "../types/Types";

export const cartSlice = createSlice({
    name: "cart",
    initialState: [],
    reducers: {
        addCart: (state: productsType[], action: any): productsType[] => {
            const cart: productsType[]  = [...state ,action.payload]
            return cart
        },
        updateCart: (state: productsType[], action: any): any => {
            const cart = state.filter((item: any) => item.id !== action.payload.id)
            return [...cart, action.payload]
        },
        removeItem: (state: productsType[], action: any): any => {
            const cart = state.filter((item: any) => item.id !== action.payload.id)
            return cart
        }
    }
})

export const {addCart, updateCart, removeItem} = cartSlice.actions
export const cartReducer =  cartSlice.reducer
