import React from 'react'
import { setCurrentUser } from '../../Slices/currentUser'
import { useDispatch, useSelector } from 'react-redux'
import { useNavigate } from 'react-router-dom'
import { stateType, userEmail, usersType } from '../../types/Types'

const useSignin = () => {
  const users = useSelector((state: stateType) => state.users)
  const dispatch = useDispatch()
  const navigate = useNavigate()

  const handleSubmit = (userData: userEmail): void => {
    const current = users.find((user) => user.username === userData.username && user.email === userData.email)
    dispatch(setCurrentUser(current!))
    navigate("/items")
  }
  return { handleSubmit }
}

export default useSignin
