import { useDispatch } from "react-redux"
import { updateCart, removeItem } from "../../Slices/cartSlice"
import { CartFunctions, Edit, productsType } from "../../types/Types"

export const useCart = () => {
  const dispatch = useDispatch()
  
  // miavorel 1 function-um u yst argumenti haskanal vor dispatch@ petq e anel
  
  // const minusItem = (arg: productsType): void => {arg.count > 1? dispatch(updateCart([arg, -1])) : dispatch(removeItem(arg))}
  // const plusItem = (arg: productsType): void => {dispatch(updateCart([arg, +1]))}
  // const deleteItem = (arg: productsType): void => {dispatch(removeItem(arg))}
  
  // return {minusItem, plusItem, deleteItem}


  function editCart(arg: productsType, arg2?: string){
    if(arg && ! arg2){
      dispatch(removeItem(arg))
    }else if(arg && arg2){
      arg2 === "plus" && dispatch(updateCart([arg, Edit.plus]))
      arg2 === "minus" && dispatch(updateCart([arg, Edit.plus]))
    }

  }
  
  return {editCart}
}
