import { useEffect } from "react"
import { apis } from "./Apis/apis"
import { useAxios } from "./Hooks/useAxios"
import { useDispatch, useSelector } from "react-redux"
import { setUser } from "./Slices/usersSlice"
import { currentType } from "./types/Types"

export const useApp = () => {
    const fetchData = useAxios(apis.users)
    // currentType
    const {current} = useSelector((state: {current: currentType}) => state.current)
    const currentUser = !!Object.keys(current).length
    const dispatch = useDispatch()
    const [loading, error, data] = useAxios(apis.users)

    useEffect(() => { 
        // dispatch(setUser(data || {}))
        fetchData()
    }, [data])
    
    return {data, current, currentUser, loading}
}
