import React from 'react'
import Item from '../Item/Item'
import { ProductsType, StateType } from '../../types/Types'
import Cart from '../Cart/Cart'
import { useItem } from './items.hook'
import Loading from '../Loading'
import { filterProductsData } from '../../helpers/helper'
import { useSelector } from 'react-redux'

const Items = () => {
  const cart: ProductsType[] = useSelector((state: StateType) => state.items)

  
  const { allData, loading } = useItem()
  const localData = filterProductsData(allData)
  
  return (
    <>
      {loading ? <Loading />
        :
        <div>
          <Cart />
          {localData?.map((item: ProductsType) => <Item key={item.id} item={item} />)}
        </div>}
    </>
  )
}

export default Items
