import React from 'react'
import { setCurrentUser } from '../../Slices/currentUser'
import { useDispatch, useSelector } from 'react-redux'
import { useNavigate } from 'react-router-dom'
import { userEmail, usersType } from '../../types/Types'

const useSignin = () => {
    const users = useSelector((state: any): any => state.users)
    const dispatch = useDispatch()
    const navigate = useNavigate()
    
        const handleSubmit = (userData: userEmail): void =>{
            const current = users.find((user: usersType) => user.username === userData.username && user.email === userData.email)
            if(current && dispatch(setCurrentUser(current))){
              navigate("/items")
            }else{
              navigate("/error")
            }
          }
  return {handleSubmit}
}

export default useSignin
