import { useEffect } from "react"
import { apis } from "../../Apis/apis"
import { useAxios } from "../../Hooks/useAxios"
import { useDispatch } from "react-redux"
import { getItems } from "../../Slices/ItemsSlice"
import { filterProductsData } from "../../helpers/helper"

export const useItem = () => {
    const dispatch = useDispatch()
    const {loading, error, allData, fetchData} = useAxios(apis.products)
    
    useEffect(() => {
        fetchData().then((res: any) => res.data)
    }, [])

    return {data: allData, loading}
}
