import { useDispatch } from "react-redux"
import { updateCart, removeItem } from "../../Slices/cartSlice"
import { productsType } from "../../types/Types"

export const useCart = (cart: productsType[]) => {
  const dispatch = useDispatch()

    const minusItem = (arg: productsType): void => {
        const count = cart.find((item) => item.id === arg.id)?.count || 0 
        if(count > 1){
          dispatch(updateCart({...arg, count: count - 1}))
        }else if(count === 1){
          dispatch(removeItem(arg)) 
        }
      }

      const plusItem = (arg: productsType): void => {
        const updatedCart: productsType[] = [...cart]
        const index = cart.indexOf(arg)
        dispatch(updateCart({...arg, count: arg.count + 1}))
        console.log(arg)
      }
    
      const deleteItem = (arg: productsType): void => {
        const filteredItem = cart.find((item: productsType) => item.id === arg.id) as productsType
        dispatch(removeItem(filteredItem))
      }
    
  return {minusItem, plusItem, deleteItem}
}
