import { addCart, updateCart } from "../../Slices/cartSlice"
import { productsType } from "../../types/Types"

export const useItem = (cart: productsType[]) => {
    const dispatch = useDispatch()
    const handleClick = (arg: productsType) => {
        const filteredItem: productsType = cart.find((item: productsType) => item.id === arg.id) as productsType
        if(!filteredItem){
          dispatch(addCart(arg))
        }else{
          // dispatch(updateCart({...arg, count: filteredItem.count! + 1}))
        }
      }
    return {handleClick}      
}
