import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { productsType } from '../../types/Types'
import { removeItem, updateCart } from '../../Slices/cartSlice'

const Cart = () => {
  const cart = useSelector((state: any) => state.cart)
  const dispatch = useDispatch()

  const minusItem = (arg: any): any => {
    const filteredItem = cart.find((item: productsType) => item.id === arg.id)
    if(filteredItem.count > 1){
      dispatch(updateCart({...arg, count: filteredItem.count - 1}))
    }else if(filteredItem.count === 1){
      dispatch(removeItem(arg)) 
    }
  }

  // const plusItem = (arg: any): any => {
  //   const updatedCart = [...cart]
  //   const filteredItem = updatedCart.find((item: productsType) => item.id === arg.id)
  //   const index = updatedCart.indexOf(filteredItem)
  //   updatedCart[index] = {...arg, count: filteredItem.count + 1}
  //   console.log(updatedCart)
  //   // dispatch(updateCart(updatedCart))
  //   dispatch(updateCart(cart))
  // }

  const plusItem = (arg: any): any => {
    const cart2 = [...cart]
    const filteredItem = cart2.find((item: productsType) => item.id === arg.id)
    const index = cart.indexOf(filteredItem)
    cart2[index].count = {count: filteredItem.count + 1}
    console.log(cart2)
    dispatch(updateCart({...arg, cart2}))
  }

  const deleteItem = (arg: any) => {
    const filteredItem = cart.find((item: productsType) => item.id === arg.id)
    dispatch(removeItem(filteredItem))
  }

  return (
    <div style={{height: "auto"}}>
      {cart.map((item: productsType, idx: number) => {
        return(
          <div key={idx} style={{display: "grid", gridTemplateColumns: "1fr"}}>
          <div>{item.title}</div>
          <div>{item.price}</div>
          <div style={{color: "red"}}>{item.count}</div>
          <div>
            {/* <img height={"30px"} src = {item.image}/> */}
          </div>
          <button style={{width: "80px"}} onClick={()=> minusItem(item)}>Minus</button>
          <button style={{width: "80px"}} onClick={()=> plusItem(item)}>Plus</button>
          <button style={{width: "80px"}} onClick={()=> deleteItem(item)}>Delete</button>
        </div>
        )
      })}
    </div>
  )
}

export default Cart
