import { useState } from "react";
import axios, { AxiosError, AxiosResponse } from "axios";
import { AxiosProduct, AxiosUser } from "../types/Types";
import { apis } from "../Apis/apis";
import { useDispatch } from "react-redux";
import { setUser } from "../Slices/usersSlice";
import { filterProductsData, filterUsersData } from "../helpers/helper";
import { getItems } from "../Slices/ItemsSlice";
import { setError } from "../Slices/errorSlice";

export const useAxios = () => {
  const [loading, setLoading] = useState(false);

  const dispatch = useDispatch()

  const fetchUsers = async () => {
    setLoading(true)
    const result: AxiosResponse<AxiosUser[]> = await axios.get(apis.users)
    try{
      dispatch(setUser(filterUsersData(result.data)))
    }catch(err: any){
      dispatch(setError(err))
    }finally{
      setLoading(false)
    }

    return result
  }

  const fetchProducts = async () => {
    setLoading(true)
    const result: AxiosResponse<AxiosProduct[]> = await axios.get(apis.products)
    try{
      dispatch(getItems(filterProductsData(result.data)))
    }catch(err: any){
      dispatch(setError(err))
    }finally{
      setLoading(false)
    }
    return result
  }

  return {loading, error, fetchProducts, fetchUsers};
};
