import { useState } from "react";
import axios, { AxiosResponse } from "axios";
import { AxiosProduct, AxiosReturn, AxiosUser } from "../types/Types";
import { apis } from "../Apis/apis";
import { useDispatch } from "react-redux";
import { setUser } from "../Slices/usersSlice";
import { filterProductsData, filterUsersData } from "../helpers/helper";
import { getItems } from "../Slices/ItemsSlice";

export const useAxios = (): any => {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const dispatch = useDispatch()

  const fetchUsers = async () => {
    setLoading(true)
    const result = await axios.get(apis.users)
    try{
      dispatch(setUser(filterUsersData(result.data)))
    }catch(err){
      setError(error)
    }finally{
      setLoading(false)
    }
    return result
  }

  const fetchProducts = async () => {
    setLoading(true)
    const result = await axios.get(apis.products)
    try{
      dispatch(getItems(filterProductsData(result.data)))
    }catch(err){
      setError(error)
    }finally{
      setLoading(false)
    }
    return result
  }

  return {loading, error, fetchProducts, fetchUsers};
};
