import React from 'react'
import { useSelector } from 'react-redux'
import { productsType, stateType } from '../../types/Types'
import { useCart } from './Cart.hook'
import uuid from 'react-uuid'
import Profile from '../Profile/Profile'

const Cart = () => {
  const cart: productsType[] = useSelector((state: stateType) => state.cart)
  
  const {minusItem, plusItem, deleteItem} = useCart(cart)
  return (
    <>
    <Profile/>
    <div style={{height: "auto"}}>
      {cart.map((item: productsType, idx: number) => {
        const {image, price, title, count} = item
        return(
          <div key={price + count} style={{display: "grid", gridTemplateColumns: "1fr"}}>
          <div>{title}</div>
          <div>{price}</div>
          <div style={{color: "red"}}>{count}</div>
          <div>
            {/* <img height={"30px"} src = {image}/> */}
          </div>
          <button style={{width: "80px"}} onClick={()=> minusItem(item)}>Minus</button>
          <button style={{width: "80px"}} onClick={()=> plusItem(item)}>Plus</button>
          <button style={{width: "80px"}} onClick={()=> deleteItem(item)}>Delete</button>
        </div>
        )
      })}
    </div>
    </>
  )
}

export default Cart
