import React from 'react'
import { Formik, Field, Form} from 'formik'
import { formType, userEmail, usersType } from '../../types/types'
import { useDispatch, useSelector } from 'react-redux'
import { setCurrentUser } from '../../Slices/currentUser'
import { Navigate, useNavigate } from 'react-router-dom'

const Signin = () => {
  const navigate = useNavigate()
  const users = useSelector((state: any): any => state.users)
  const currentUser = useSelector((state: any) => state.currentUser)
  
  const dispatch = useDispatch()

  const initial: userEmail = {
    username: "",
    email: ""
  }

  const handleSubmit = (userData: userEmail): any =>{
    const current = users.find((user: usersType) => user.username === userData.username && user.email === userData.email)
    current && dispatch(setCurrentUser(current))
  }

  function validateEmail(value: string): formType{
    let error: formType
      if(!value){
      error = "enter email"
      
      }else if(!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(value)){
        error = "invalid email address"
      }
      return error
    }
    
    function validateUsername(value: string): formType{
      let error: formType
      if (!value) {
        error = "enter username"
      }
      return error
    }

  return (

    <div style={{width: "100px"}}>
      <h1>Signup</h1>
      <Formik initialValues = {initial}
       onSubmit={values => {
          handleSubmit(values)
       }}>
         {({ errors, touched }) => (
         <Form>
          Email
           <Field name="email" validate={validateEmail}/>
           {errors.email && touched.email && <div style={{color: "red"}}>{errors.email}</div>}
           Username
           <Field name="username" validate={validateUsername} />
           {errors.username && touched.username && <div style={{color: "red"}}>{errors.username}</div>}
           <button type="submit">Submit</button>
         </Form>
       )}
       </Formik>
    </div>
  )
  
}

export default Signin
