import { useDispatch } from "react-redux"
import { addCart, updateCart } from "../../Slices/cartSlice"
import { productsType } from "../../types/Types"
import { useCart } from "../Cart/Cart.hook"

export const useItem = (cart: productsType[]) => {
    const dispatch = useDispatch()
    
    const handleClick = (arg: productsType) => {
      const filteredItem: productsType = cart.find((item) => item.id === arg.id) as productsType

      console.log(2222222,filteredItem,cart)

      dispatch(filteredItem && !!Object.keys(filteredItem).length ? updateCart([arg, +1]) : addCart(arg))
    }
  return {handleClick}
}
