import { useEffect } from "react"
import { apis } from "./Apis/apis"
import { useAxios } from "./Hooks/useAxios"
import { useDispatch, useSelector } from "react-redux"
import { getUser } from "./Slices/usersSlice"
// import { filterUsersData } from "./helpers/filterUsersData"
import { currentType } from "./types/Types"

export const useApp = () => {
    const {current}: currentType = useSelector((state: any) => state.current)
    const currentUser = !!Object.keys(current).length
    const dispatch = useDispatch()
    const [loading, error, data] = useAxios(apis.users)

    useEffect(() => {data && dispatch(getUser(filterUsersData(data)))}, [data])
    return {data, current, currentUser}
}
