import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { addCart, updateCart } from '../../Slices/cartSlice'
import { productsType } from '../../types/Types'

const Item = ({item}: {item :any}) => {
  const cart = useSelector((state: any) => state.cart)
  const dispatch = useDispatch()

  const handleClick = (arg: any) => {
    const filteredItem = cart.find((item: productsType) => item.id === arg.id)
    console.log(filteredItem.count)
    if(!filteredItem){
      dispatch(addCart(arg))

    }else{
      dispatch(updateCart({...arg, count: filteredItem.count + 1}))
    }
  }

  return (
    <>
      <div style={{display: "grid", gridTemplateColumns: "3fr 1fr 1fr"}}>
        <h5>{item.title}</h5>
      <div>
        {/* <img style={{height: "50px"}} src = {item.image}/> */}
      </div>
        <p>{item.price} $</p>
    </div>
      <button onClick={() => handleClick(item)}>ADD</button>
    </>
  )
}

export default Item
