import React from 'react'
import { useEffect } from "react"
import { useAxios } from "./Hooks/useAxios"
import { useSelector } from "react-redux"
import { CurrentType, StateType } from "./types/Types"

export const useApp = () => {
    const {current} = useSelector((state: any) => state.current)
    
    // const state = useSelector((state: any) => state.current)

    const currentUser = !!Object.keys(current).length
    const {fetchUsers} = useAxios()
    
    useEffect(()=> {fetchUsers()}, [])

    return {current, currentUser}
}
